// client/src/components/AdminPanel.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPanel = () => {
  const [coupons, setCoupons] = useState([]);
  const [errorMsg, setErrorMsg] = useState('');

  useEffect(() => {
    const fetchCoupons = async () => {
      setErrorMsg('');
      try {
        // UPDATED: Use relative URL so proxy settings are used
        const res = await axios.get('/api/coupons');
        setCoupons(res.data);
      } catch (err) {
        console.error('Error fetching coupons:', err.response ? err.response.data : err.message);
        setErrorMsg('Unable to fetch coupons');
      }
    };
    fetchCoupons();
  }, []);

  return (
    <div>
      <h2>Admin Panel</h2>
      {errorMsg && <p style={{ color: 'red' }}>{errorMsg}</p>}
      <table border="1" cellPadding="8" style={{ margin: '0 auto' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Mobile Number</th>
            <th>Flat Number</th>
            <th>Expiration</th>
            <th>Used</th>
            <th>Created At</th>
          </tr>
        </thead>
        <tbody>
          {coupons.length > 0 ? (
            coupons.map((coupon) => (
              <tr key={coupon._id}>
                <td>{coupon._id}</td>
                <td>{coupon.name}</td>
                <td>{coupon.mobileNumber}</td>
                <td>{coupon.flatNumber}</td>
                <td>{new Date(coupon.expireAt).toLocaleString()}</td>
                <td>{coupon.used ? 'Yes' : 'No'}</td>
                <td>{new Date(coupon.createdAt).toLocaleString()}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7">No coupons found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
