import React, { useState } from 'react';
import axios from 'axios';
import QRCodeDisplay from './QRCodeDisplay';

const CouponForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    mobileNumber: '',
    flatNumber: '',
    expireAt: ''
  });
  const [qrData, setQrData] = useState('');
  const [couponCreated, setCouponCreated] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');

  const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    setErrorMsg('');
    try {
      const res = await axios.post('http://localhost:5000/api/coupons', formData);
      setQrData(res.data.qrData);
      setCouponCreated(true);
    } catch (err) {
      setErrorMsg(err.response?.data?.msg || 'Server error');
    }
  };

  return (
    <div className="form-container">
      <h1>Create a Coupon</h1>
      <form onSubmit={onSubmit}>
        {['name', 'mobileNumber', 'flatNumber', 'expireAt'].map((field, index) => (
          <div key={index}>
            <label>{field.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}:</label>
            <input
              type={field === 'expireAt' ? 'datetime-local' : 'text'}
              name={field}
              value={formData[field]}
              onChange={onChange}
              required
            />
          </div>
        ))}
        <button type="submit">Generate QR Code</button>
      </form>
      {errorMsg && <p style={{ color: 'red' }}>{errorMsg}</p>}
      {couponCreated && (
        <div className="qr-code-container">
          <h3>Your Coupon QR Code</h3>
          <QRCodeDisplay qrData={qrData} />
        </div>
      )}
    </div>
  );
};

export default CouponForm;
