import React, { useState } from 'react';
import CouponForm from './components/CouponForm';
import AdminPanel from './components/AdminPanel';
import AdminLogin from './components/AdminLogin';
import './App.css';

function App() {
  const [view, setView] = useState('form'); // 'form' or 'admin'
  const [adminAuthenticated, setAdminAuthenticated] = useState(false);

  const handleAdminLogin = () => setAdminAuthenticated(true);

  return (
    <div className="App">
      <header>
        <h1>Dynamic QR Coupon System</h1>
        <nav>
          <button onClick={() => setView('form')}>Coupon Form</button>
          <button onClick={() => setView('admin')}>Admin Panel</button>
        </nav>
      </header>
      <main>
        {view === 'form' ? (
          <CouponForm />
        ) : !adminAuthenticated ? (
          <AdminLogin onLogin={handleAdminLogin} />
        ) : (
          <AdminPanel />
        )}
      </main>
    </div>
  );
}

export default App;
