// client/src/components/AdminPanel.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPanel = () => {
  const [coupons, setCoupons] = useState([]);
  const [errorMsg, setErrorMsg] = useState('');

  useEffect(() => {
    const fetchCoupons = async () => {
      setErrorMsg('');
      try {
        const res = await axios.get('/api/coupons');
        setCoupons(res.data);
      } catch (err) {
        console.error('Error fetching coupons:', err.response ? err.response.data : err.message);
        setErrorMsg('Unable to fetch coupons');
      }
    };
    fetchCoupons();
  }, []);

  return (
    <div className="table-container">
      <h2>Admin Panel</h2>
      {errorMsg && <p style={{ color: 'red' }}>{errorMsg}</p>}
      <table border="1" cellPadding="8" style={{ margin: '0 auto' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Mobile Number</th>
            <th>Flat Number</th>
                      <th>Used</th>
            <th>Created At</th>
          </tr>
        </thead>
        <tbody>
          {coupons.length > 0 ? (
            coupons.map(({ _id, name, mobileNumber, flatNumber, expireAt, used, createdAt }) => (
              <tr key={_id}>
                <td>{_id}</td>
                <td>{name}</td>
                <td>{mobileNumber}</td>
                <td>{flatNumber}</td>
                <td>{new Date(expireAt).toLocaleString()}</td>
                <td>{used ? 'Yes' : 'No'}</td>
                <td>{new Date(createdAt).toLocaleString()}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7">No coupons found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
