// client/src/components/CouponForm.js
import React, { useState } from 'react';
import axios from 'axios';
import QRCodeDisplay from './QRCodeDisplay';

const CouponForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    mobileNumber: '',
    flatNumber: '',
    expireAt: ''  // expects ISO datetime string
  });
  const [qrData, setQrData] = useState('');
  const [couponCreated, setCouponCreated] = useState(false);

  const { name, mobileNumber, flatNumber, expireAt } = formData;

  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/coupons', formData);
      setQrData(res.data.qrData);
      setCouponCreated(true);
    } catch (err) {
      console.error(err.response ? err.response.data : err.message);
    }
  };

  return (
    <div>
      <h2>Create a Coupon</h2>
      <form onSubmit={onSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={name} onChange={onChange} required />
        </div>
        <div>
          <label>Mobile Number:</label>
          <input type="text" name="mobileNumber" value={mobileNumber} onChange={onChange} required />
        </div>
        <div>
          <label>Flat Number:</label>
          <input type="text" name="flatNumber" value={flatNumber} onChange={onChange} required />
        </div>
        <div>
          <label>Expiration Date & Time:</label>
          {/* Use datetime-local for date and time input */}
          <input type="datetime-local" name="expireAt" value={expireAt} onChange={onChange} required />
        </div>
        <button type="submit">Generate QR Code</button>
      </form>
      {couponCreated && (
        <div>
          <h3>Your Coupon QR Code (scan to apply):</h3>
          <QRCodeDisplay qrData={qrData} />
        </div>
      )}
    </div>
  );
};

export default CouponForm;
